{
  "key_formulas": [
    {
      "name": "Reynolds Number",
      "formula": "Re = (ρ * u * L) / μ",
      "description": "Dimensionless quantity used to predict flow patterns in fluid flow situations, indicating the ratio of inertial forces to viscous forces.",
      "usage_context": "Used to determine whether flow is laminar or turbulent, and in boundary layer analysis."
    },
    {
      "name": "Prandtl Number",
      "formula": "Pr = μ * cp / k",
      "description": "Dimensionless number relating momentum diffusivity to thermal diffusivity.",
      "usage_context": "Important in convective heat transfer calculations to determine the relative thickness of thermal and velocity boundary layers."
    },
    {
      "name": "Nusselt Number",
      "formula": "Nu = h * L / k",
      "description": "Dimensionless number representing the ratio of convective to conductive heat transfer.",
      "usage_context": "Used to calculate the convective heat transfer coefficient in forced convection problems."
    },
    {
      "name": "Schmidt Number",
      "formula": "Sc = μ / (ρ * D)",
      "description": "Dimensionless number relating momentum diffusivity to mass diffusivity.",
      "usage_context": "Important in mass transfer applications, analogous to the Prandtl number in heat transfer."
    },
    {
      "name": "Chilton-Colburn Analogy",
      "formula": "St * Pr^(2/3) = Cf / 2",
      "description": "Relates heat transfer coefficient to skin friction coefficient.",
      "usage_context": "Used when both heat transfer and momentum transfer occur simultaneously, particularly in turbulent flow."
    },
    {
      "name": "Boundary Layer Thickness",
      "formula": "δ ≈ 5.0 * x / √(Rex)",
      "description": "Approximate expression for laminar boundary layer thickness.",
      "usage_context": "Used to estimate the boundary layer thickness in laminar flow over flat plates."
    },
    {
      "name": "Sherwood Number",
      "formula": "Sh = hm * L / D",
      "description": "Dimensionless number representing the ratio of convective to diffusive mass transfer.",
      "usage_context": "Used in mass transfer problems analogous to the Nusselt number in heat transfer."
    }
  ]
}
