{
  "key_formulas": [
    {
      "name": "Reynolds Number",
      "formula": "Re = (ρ * u * L) / μ",
      "description": "Dimensionless quantity that predicts flow patterns in different fluid flow situations.",
      "usage_context": "Used to determine whether flow is laminar or turbulent. Critical for boundary layer analysis."
    },
    {
      "name": "Prandtl Number",
      "formula": "Pr = (μ * cp) / k",
      "description": "Ratio of momentum diffusivity to thermal diffusivity.",
      "usage_context": "Important for determining relative thickness of velocity vs thermal boundary layers."
    },
    {
      "name": "Nusselt Number",
      "formula": "Nu = (h * L) / k",
      "description": "Ratio of convective to conductive heat transfer at a boundary.",
      "usage_context": "Used to calculate convection heat transfer coefficients in forced convection problems."
    },
    {
      "name": "Stanton Number",
      "description": "Dimensionless heat transfer coefficient.",
      "formula": "St = Nu / (Re * Pr)",
      "usage_context": "Central to Reynolds analogy, relating friction factor to heat transfer."
    },
    {
      "name": "Chilton-Colburn Analogy",
      "formula": "St * Pr^(2/3) = Cf/2",
      "description": "Modified Reynolds analogy for heat and mass transfer.",
      "usage_context": "Used when Pr ≠ 1 to relate skin friction to heat transfer coefficient."
    },
    {
      "name": "Boundary Layer Thickness",
      "formula": "δ ≈ 5x / √Re_x",
      "description": "Approximate thickness of velocity boundary layer for laminar flow.",
      "usage_context": "Flat plate laminar flow calculations, valid for Re < 5×10^5."
    }
  ]
}
