## Key Formulas for the Study Plan

```json
{
  "key_formulas": [
    {
      "name": "Reynolds Number (Re)",
      "formula": "Re = (ρ * u * L) / μ",
      "description": "Dimensionless number used to predict flow patterns in fluid dynamics.",
      "usage_context": "Determines whether flow is laminar or turbulent. Critical for boundary layer analysis."
    },
    {
      "name": "Prandtl Number (Pr)",
      "formula": "Pr = μ * c_p / k",
      "description": "Ratio of momentum diffusivity to thermal diffusivity.",
      "usage_context": "Relates velocity boundary layer to thermal boundary layer in heat transfer problems."
    },
    {
      "name": "Schmidt Number (Sc)",
      "formula": "Sc = μ / (ρ * D_AB)",
      "description": "Ratio of momentum diffusivity to mass diffusivity.",
      "usage_context": "Relates velocity boundary layer to concentration boundary layer in mass transfer."
    },
    {
      "name": "Nusselt Number (Nu)",
      "formula": "Nu = h * L / k",
      "description": "Ratio of convective to conductive heat transfer.",
      "usage_context": "Used to calculate heat transfer coefficient in convective systems."
    },
    {
      "name": "Sherwood Number (Sh)",
      "formula": "Sh = h_m * L / D_AB",
      "description": "Ratio of convective to diffusive mass transfer.",
      "usage_context": "Used to calculate mass transfer coefficient in convective systems."
    },
    {
      "name": "Skin Friction Coefficient (Cf)",
      "formula": "Cf = τ_w / (0.5 * ρ * u²)",
      "description": "Dimensionless wall shear stress.",
      "usage_context": "Quantifies drag force in boundary layer flows."
    },
    {
      "name": "Reynolds Analogy",
      "formula": "Cf/2 = St = St_m",
      "description": "Relates friction factor to heat and mass transfer coefficients.",
      "usage_context": "Simplified model for turbulent flow when Pr ≈ Sc ≈ 1."
    },
    {
      "name": "Chilton-Colburn Analogy",
      "formula": "j_H = j_m = Cf/2",
      "description": "Extended analogy for heat and mass transfer.",
      "usage_context": "Applicable when Pr ≠ 1 and Sc ≠ 1, using j-factors."
    },
    {
      "name": "Local Heat Flux",
      "formula": "q'' = h * (T_s - T_∞)",
      "description": "Heat transfer rate per unit area.",
      "usage_context": "Calculates convective heat transfer at a specific location."
    },
    {
      "name": "Mass Flux",
      "formula": "N_A = h_m * (C_A,s - C_A,∞)",
      "description": "Mass transfer rate per unit area.",
      "usage_context": "Calculates convective mass transfer at a specific location."
    }
  ]
}
