{
  "key_formulas": [
    {
      "name": "Reynolds Number (Re)",
      "formula": "Re = (ρ * u * L) / μ",
      "description": "Ratio of inertial forces to viscous forces in fluid flow.",
      "usage_context": "Used to predict flow regimes (laminar/turbulent) and analyze boundary layer behavior."
    },
    {
      "name": "Prandtl Number (Pr)",
      "formula": "Pr = (μ * c_p) / k",
      "description": "Ratio of momentum diffusivity to thermal diffusivity.",
      "usage_context": "Determines relative thickness of velocity and thermal boundary layers."
    },
    {
      "name": "Schmidt Number (Sc)",
      "formula": "Sc = μ / (ρ * D_AB)",
      "description": "Ratio of momentum diffusivity to mass diffusivity.",
      "usage_context": "Analyzes mass transfer problems and concentration boundary layers."
    },
    {
      "name": "Nusselt Number (Nu)",
      "formula": "Nu = (h * L) / k",
      "description": "Ratio of convective to conductive heat transfer at a boundary.",
      "usage_context": "Quantifies heat transfer enhancement due to convection."
    },
    {
      "name": "Reynolds Analogy",
      "formula": "St = (Cf / 2)",
      "description": "Relates momentum transfer to heat transfer in turbulent flow.",
      "usage_context": "Simplifies calculation of heat transfer coefficients from friction factors."
    },
    {
      "name": "Sherwood Number (Sh)",
      "formula": "Sh = (h_m * L) / D_AB",
      "description": "Mass transfer equivalent of the Nusselt number.",
      "usage_context": "Quantifies mass transfer enhancement due to convection."
    },
    {
      "name": "Lewis Number (Le)",
      "formula": "Le = Sc / Pr",
      "description": "Ratio of thermal diffusivity to mass diffusivity.",
      "usage_context": "Relates heat and mass transfer boundary layers in simultaneous processes."
    },
