    {
      "name": "Reynolds Number",
      "formula": "Re = (ρ * u * L)/μ",
      "description": "Dimensionless number that characterizes flow regimes (laminar vs. turbulent)",
      "usage_context": "Used to determine flow characteristics in convection problems, typically with Re_crit ≈ 5×10^5 for flat plate transition"
    },
    {
      "name": "Prandtl Number",
      "formula": "Pr = ν/α = (μ * c_p)/k",
      "description": "Ratio of momentum diffusivity to thermal diffusivity",
      "usage_context": "Important for determining relative thickness of velocity and thermal boundary layers in heat transfer problems"
    },
    {
      "name": "Schmidt Number",
      "Sc": "Sc = ν/D = μ/(ρ * D_AB)",
      "description": "Ratio of momentum diffusivity to mass diffusivity",
      "usage_context": "Used in mass transfer problems to relate velocity and concentration boundary layers"
    },
    {
      "name": "Nusselt Number",
      "formula": "Nu = (h * L)/k",
      "description": "Dimensionless heat transfer coefficient",
      "usage_context": "Relates convective to conductive heat transfer at a boundary, used to calculate heat transfer rates"
    },
    {
      "name": "Sherwood Number",
      "formula": "Sh = (h_m * L)/D_AB",
      "description": "Dimensionless mass transfer coefficient",
      "usage_context": "Mass transfer equivalent of Nusselt number, used to calculate mass transfer rates"
    },
    {
      "name": "Skin Friction Coefficient",
      "formula": "C_f = τ_w/(0.5 * ρ * u_∞^2)",
      "description": "Dimensionless wall shear stress",
      "usage_context": "Used to quantify drag force due to viscous effects in boundary layers"
    },
    {
      "name": "Reynolds Analogy",
      "formula": "St = C_f/2",
      "description": "Relates momentum transfer to heat transfer",
      "usage_context": "Used when Pr ≈ 1 to estimate heat transfer coefficients from known friction coefficients"
    }
  ]
}
